bool checkIfPangram(char* sentence)
{
    return strchr(sentence,'a')&&strchr(sentence,'b')&&strchr(sentence,'c')&&strchr(sentence,'d')&&strchr(sentence,'e')&&strchr(sentence,'f')&&strchr(sentence,'g')&&strchr(sentence,'h')&&strchr(sentence,'i')&&strchr(sentence,'j')&&strchr(sentence,'k')&&strchr(sentence,'l')&&strchr(sentence,'m')&&strchr(sentence,'n')&&strchr(sentence,'o')&&strchr(sentence,'p')&&strchr(sentence,'q')&&strchr(sentence,'r')&&strchr(sentence,'s')&&strchr(sentence,'t')&&strchr(sentence,'u')&&strchr(sentence,'v')&&strchr(sentence,'w')&&strchr(sentence,'x')&&strchr(sentence,'y')&&strchr(sentence,'z');
}
